var binding = require('./build/Release/license');

exports.getHddId = function(cb) {
    if (cb) {
      return binding.GetHddId(cb);
    }
    return binding.GetHddId();
};

exports.getBiosId = function() {
  return binding.GetBiosId();
};

exports.macAddressExists = function(macaddr) {
  return binding.MacAddressExists(macaddr);
};

exports.getMacAddress = function() {
  return binding.GetMacAddress();
};

exports.getSerial = function() {
  return binding.GetSerial();
};

exports.getUuid = function() {
  return binding.GetUuid();
};

exports.encrypt = function(value, initVector) {
    return binding.Encrypt(value, initVector);
};

exports.decrypt = function(msg, initVector) {
    return binding.Decrypt(msg, initVector);
};